mapper.update:
UPDATE inno_odds.refactor_prematch rp


        rp.bet188_id = #{match.bet188Id},


        rp.betradar_id = = #{match.betradarId},


        rp.betgenius_id = #{match.betgeniusId}


WHERE id = #{id}

mapper.select:
SELECT rp.id, rp.tournament_id, rp.home_id, rp.away_id, rp.bet188_id, rp.betradar_id, rp.betgenius_id
FROM inno_odds.refactor_prematch rp
WHERE id IN (#{id1}, #{id2});
